import React from "react";
import { ObjectSchema } from "yup";
interface Context {
    [key: string]: any;
}
interface Helpers {
    resetForm: () => void;
}
interface Props {
    initialData?: object;
    children: React.ReactNode;
    context?: Context;
    schema?: ObjectSchema<object>;
    onSubmit: (data: object, helpers: Helpers) => void;
}
export default function Form({ initialData, children, schema, context, onSubmit }: Props): JSX.Element;
export {};
